mapper.select:
SELECT COMMUNITY_SEQ, USER_ID, NICKNAME, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE FROM( SELECT ROW_NUMBER()OVER(ORDER BY WDATE DESC) AS RNUM, COMMUNITY_SEQ, USER_ID, NICKNAME, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE FROM COMMUNITY_BBS A, MEMBER B WHERE 1=1 AND BBSTYPE = 'hello' AND A.USER_ID = B.ID AND DEL = 0
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND B.NICKNAME=#{search}
ORDER BY COMMUNITY_SEQ DESC ) WHERE RNUM BETWEEN #{start} AND #{end}

	SELECT  COMMUNITY_SEQ, USER_ID, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE,
             (
             	SELECT COUNT(*) FROM COMMUNITY_BBS_LIKE WHERE COMMUNITY_BBS_LIKE.USER_ID = #{user_id}     
             	AND COMMUNITY_BBS_LIKE.COMMUNITY_SEQ = COMMUNITY_BBS.COMMUNITY_SEQ     
             ) AS LIKED_YN, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME
	FROM COMMUNITY_BBS
	WHERE COMMUNITY_SEQ = #{community_seq}

mapper.update:
UPDATE COMMUNITY_BBS
SET READCOUNT = READCOUNT + 1
WHERE COMMUNITY_SEQ = #{community_seq}

mapper.select:
SELECT COUNT(*) AS TOTAL
FROM COMMUNITY_BBS_LIKE
WHERE COMMUNITY_SEQ = #{community_seq}

		SELECT * FROM COMMUNITY_BBS_COMMENT
  		WHERE COMMUNITY_SEQ = #{community_seq}
  		ORDER BY COMMENT_SEQ DESC

SELECT COMMUNITY_SEQ, USER_ID, NICKNAME, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE FROM( SELECT ROW_NUMBER()OVER(ORDER BY WDATE DESC) AS RNUM, COMMUNITY_SEQ, USER_ID, NICKNAME, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE FROM COMMUNITY_BBS A, MEMBER B WHERE 1=1 AND BBSTYPE = 'free' AND A.USER_ID = B.ID AND DEL = 0
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND B.NICKNAME=#{search}
ORDER BY COMMUNITY_SEQ DESC ) WHERE RNUM BETWEEN #{start} AND #{end}

SELECT  COMMUNITY_SEQ, USER_ID, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE,
(
SELECT COUNT(*) FROM COMMUNITY_BBS_LIKE WHERE COMMUNITY_BBS_LIKE.USER_ID = #{user_id} 
AND COMMUNITY_BBS_LIKE.COMMUNITY_SEQ = COMMUNITY_BBS.COMMUNITY_SEQ
) AS LIKED_YN, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME
FROM COMMUNITY_BBS
WHERE COMMUNITY_SEQ = #{community_seq}
