SELECT ID, USERNAME, NICKNAME, PHONE, EMAIL, CREATE_DATE, SNS_TYPE ,AUTH
FROM (
SELECT ROW_NUMBER()OVER(ORDER BY USERNAME DESC) AS RNUM,
ID, USERNAME, NICKNAME, PHONE, EMAIL, CREATE_DATE, SNS_TYPE, AUTH
FROM MEMBER
WHERE 1=1


        AND ID = #{search}


        AND USERNAME = #{search}


        AND NICKNAME = #{search}


ORDER BY USERNAME DESC
)
WHERE RNUM BETWEEN #{start} AND #{end}

SELECT NVL(COUNT(*), 0)
FROM MEMBER
WHERE 1=1


        AND ID = #{search}


        AND USERNAME = #{search}


        AND NICKNAME = #{search}

SELECT COMMUNITY_SEQ, USER_ID, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE, (SELECT COUNT(*) FROM COMMUNITY_BBS_COMMENT WHERE A.COMMUNITY_SEQ = COMMUNITY_BBS_COMMENT.COMMUNITY_SEQ AND DEL = 0) AS COMMENTCOUNT FROM ( SELECT ROW_NUMBER()OVER(ORDER BY WDATE DESC) AS RNUM, COMMUNITY_SEQ, TITLE, WDATE, CONTENT, READCOUNT, LIKECOUNT, DEL, BBSTYPE, USER_ID FROM COMMUNITY_BBS WHERE 1=1 AND DEL = 0 AND BBSTYPE = #{bbstype}
AND TITLE LIKE '%'||#{search}||'%'
AND CONTENT LIKE '%'||#{search}||'%'
AND (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) = #{search}
ORDER BY WDATE DESC ) A WHERE RNUM BETWEEN #{start} AND #{end}

SELECT ORDER_SEQ, USER_ID, ORDER_DATE, PRODUCT_SEQ, OPTION1_SEQ, OPTION2_SEQ, QUANTITY, RENT_SEQ, RENT_SDATE, RENT_EDATE, PRODUCT_PRICE, SHIPPING_FEE, TOTAL_PRICE, RECEIVER, ADDRESS, RECEIVER_PHONE, MEMO, PAYMENT, MERCHANT_UID, ORDER_STATUS, REVIEW_STATUS, INVOICE_NUMBER AS INVOICENUMBER, (SELECT PRODUCT_NAME FROM PRODUCT WHERE PRODUCT_SEQ = P.PRODUCT_SEQ) AS PRODUCT_NAME, (SELECT THUMBNAIL_NAME FROM PRODUCT WHERE PRODUCT_SEQ = P.PRODUCT_SEQ) AS THUMBNAIL_NAME, (SELECT OPTION_NAME FROM PRODUCT_OPTION WHERE OPTION_SEQ = P.OPTION1_SEQ) AS OPTION1_NAME, (SELECT OPTION_NAME FROM PRODUCT_OPTION WHERE OPTION_SEQ = P.OPTION2_SEQ) AS OPTION2_NAME, (SELECT RENT_NAME FROM PRODUCT_RENT WHERE RENT_SEQ = P.RENT_SEQ) AS RENT_NAME, (SELECT USERNAME FROM MEMBER WHERE ID = USER_ID) AS USERNAME, (SELECT PHONE FROM MEMBER WHERE ID = USER_ID) AS PHONE FROM ( SELECT ROW_NUMBER()OVER(ORDER BY ORDER_DATE DESC) AS RNUM, ORDER_SEQ, USER_ID, ORDER_DATE, PRODUCT_SEQ, OPTION1_SEQ, OPTION2_SEQ, QUANTITY, RENT_SEQ, RENT_SDATE, RENT_EDATE, PRODUCT_PRICE, SHIPPING_FEE, TOTAL_PRICE, RECEIVER, ADDRESS, RECEIVER_PHONE, MEMO, PAYMENT, MERCHANT_UID, ORDER_STATUS, REVIEW_STATUS, INVOICE_NUMBER, (SELECT USERNAME FROM MEMBER WHERE ID = USER_ID) AS USERNAME, (SELECT PHONE FROM MEMBER WHERE ID = USER_ID) AS PHONE FROM PRODUCT_ORDER WHERE 1=1
AND ORDER_STATUS = 1
AND ORDER_STATUS = 2
AND ORDER_STATUS = 3
AND ORDER_STATUS = 4
AND (SELECT USERNAME FROM MEMBER WHERE ID = USER_ID) = #{search}
AND (SELECT PHONE FROM MEMBER WHERE ID = USER_ID) = #{search}
ORDER BY ORDER_DATE DESC ) P WHERE RNUM BETWEEN #{start} AND #{end}
