mapper.select:
SELECT REVIEW_SEQ, CONTENTID, TITLE, CONTENT, WDATE,READCOUNT, USER_ID,
REVISIT, LIKE_COUNT, DEL, NICKNAME, COMMENTCOUNT FROM CAMPING_BBS WHERE REVIEW_SEQ = #{review_seq}
AND CONTENTID = #{contentid}

SELECT * FROM (SELECT ROW_NUMBER()OVER(ORDER BY READCOUNT DESC) AS RNUM, CONTENTID, FACLTNM, ADDR1, FIRSTIMAGEURL, LINEINTRO, TEL, HOMEPAGE, SBRSCL, READCOUNT, LIKECOUNT, CREATEDTIME, MODIFIEDTIME FROM CAMPING_LIST WHERE 1=1
AND (ADDR1 LIKE '%'||#{searchWord}||'%' OR FACLTNM LIKE '%'||#{searchWord}||'%')
) WHERE RNUM BETWEEN ${start} AND ${end} ORDER BY READCOUNT DESC
