mapper.select:
SELECT NVL(COUNT(*), 0)
FROM PRODUCT_ORDER
WHERE USER_ID = #{id}

SELECT QNA_SEQ, PRODUCT_SEQ, USER_ID, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME, TITLE, CONTENT, WDATE, SECRET, DEL, STATUS, REF, STEP, NVL(( SELECT CONTENT FROM PRODUCT_QNA WHERE REF = P.REF AND STEP = 1 ),0)AS ANSWERCONTENT, ( SELECT WDATE FROM PRODUCT_QNA WHERE REF = P.REF AND STEP = 1 ) AS ANSWERWDATE FROM( SELECT ROW_NUMBER()OVER(ORDER BY WDATE DESC) AS RNUM, QNA_SEQ, PRODUCT_SEQ, USER_ID, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME, TITLE, CONTENT, WDATE, SECRET, DEL, STATUS, REF, STEP FROM PRODUCT_QNA WHERE DEL = 0 AND STEP = 0 AND (SELECT ID FROM MEMBER WHERE PRODUCT_QNA.USER_ID = MEMBER.ID) = #{id}
AND STATUS = 0
AND STATUS = 1
ORDER BY WDATE DESC, REF DESC ) P WHERE RNUM BETWEEN #{start} AND #{end}

SELECT COUNT(*) FROM PRODUCT_QNA WHERE 1=1 AND DEL = 0 AND STEP = 0 AND (SELECT ID FROM MEMBER WHERE PRODUCT_QNA.USER_ID = MEMBER.ID) = #{id}
AND STATUS = 0
AND STATUS = 1

SELECT QNA_SEQ, USER_ID, (SELECT NICKNAME FROM MEMBER WHERE ID = USER_ID) AS NICKNAME, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, READCOUNT, STATUS, SECRET, DEL, NVL(( SELECT TITLE FROM QNA WHERE REF = P.REF AND STEP = 1),0)AS ANSWERTITLE, NVL(( SELECT CONTENT FROM QNA WHERE REF = P.REF AND STEP = 1),0)AS ANSWERCONTENT, ( SELECT WDATE FROM QNA WHERE REF = P.REF AND STEP = 1 ) AS ANSWERWDATE FROM ( SELECT ROW_NUMBER()OVER(ORDER BY REF DESC, STEP ASC) AS RNUM, QNA_SEQ, USER_ID, REF, STEP, DEPTH, TITLE, CONTENT, WDATE, READCOUNT, STATUS, SECRET, DEL FROM QNA WHERE 1=1 AND STEP = 0 AND DEL = 0 AND (SELECT ID FROM MEMBER WHERE QNA.USER_ID = MEMBER.ID) = #{id}
AND STATUS = 0
AND STATUS = 1
ORDER BY REF DESC, STEP ASC ) P WHERE RNUM BETWEEN #{start} AND #{end}

SELECT COUNT(*) 
FROM PRODUCT_ORDER
WHERE USER_ID = #{id}
	AND ORDER_STATUS = #{status}
