mapper.select:
SELECT AVG(RATE) 
FROM PRODUCT_REVIEW
WHERE PRODUCT_SEQ = #{product_Seq}

mapper.update:
UPDATE PRODUCT_ORDER
SET REVIEW_STATUS = 1
WHERE ORDER_SEQ = #{order_Seq}

		UPDATE PRODUCT
		SET DEL = 1
  		WHERE PRODUCT_SEQ = #{product_Seq}

UPDATE PRODUCT
SET PRODUCT_STOCK = PRODUCT_STOCK - #{quantity}
WHERE PRODUCT_SEQ = #{product_Seq}
